                DB::insert('
            INSERT INTO `provinces` (`id`, `country_id`, `name`, `description`, `content`, `keywords`, `latitude`, `longitude`, `created_at`, `updated_at`) VALUES
            (1, 244, \'آذربایجان شرقی\', \'استان آذربایجان شرقی در منطقه&zwnj;ای کوهستانی واقع شده است که سطح آن را کوهستان، تپه ماهورها و زمین&zwnj;های هموار شامل دشت&zwnj;ها و جلگه&zwnj;ها تشکیل می&zwnj;دهند. از جمله ارتفاعات مشهور این استان می&zwnj;توان رشته&zwnj;کوه&zwnj;های آتشفشانی سبلان و سهند، رشته&zwnj;کوه&zwnj;های قره&zwnj;داغ، تخت سلیمان، اربط، بزقوش و قوشاداغ را نام برد. علاوه بر جاذبه&zwnj;های طبیعی، این استان شامل جاذبه&zwnj;های مذهبی، تاریخی، تفریحی و فرهنگی نیز می&zwnj;باشد. جاذبه&zwnj;های تاریخی استان آذربایجان شرقی نیز بسیار قابل توجه است؛ جاذبه&zwnj;هایی چون ارگ تبریز، ایل گلی، بازار تبریز که بزرگترین بازار سرپوشیده جهان است و معماری منحصر به فردی دارد. مسجد جامع تبریز، مقبره&zwnj;الشعرا، بقعه سید حمزه و کلیسای سن استپانوس از جمله جاذبه&zwnj;های مذهبی این استان است. از جمله جاذبه&zwnj;های تفریحی تبریز نیز می&zwnj;توان کاخ شهرداری، میدان شهرداری، سد مخزنی قلعه چای و آسیاب خرابه را نام برد. همچنین، جاذبه&zwnj;هایی چون مقبره سعدالدین محمود بن عبدالکریم بن یحیی، مقبره&zwnj;الشعرا، موزه آذربایجان و موزه قاجار از جمله جاذبه&zwnj;های فرهنگی استان آذربایجان شرقی است. بنابراین، سفر به این استان فرصت دیدین جاذبه&zwnj;های متنوعی را برایتان فراهم می&zwnj;آورد.\', \'آذربایجان شرقی\', \'\', \'38.09210042855369\', \'46.287404009523584\', NULL, NULL),
            (2, 244, \'آذربایجان غربی\', \'استان آذربایجان غربی، دارای تمدن 8 هزار ساله است که حدود 1517 اثر ملی ثبت شده دارد. روستاهای زیبایی در این استان وجود دارد که به عنوان روستاهای هدف گردشگری مورد توجه گردشگران قرار گرفته است؛ روستاهایی چون روستای سرخ، روستای زمزیران، روستای رند، روستاهای اطراف دریاچه ارومیه و غیره. بزرگترین دریاچه داخلی ایران با نام دریاچه ارومیه در این استان واقع است. اولین کلیسای جامع جهان مسیحیت با نام قره کلیسا، دروازه سنگی خوی، جزیره شناور ایران، دریاچه تخت سلیمان، آبشار شلماش، طبیعت بکر دریاچه مارمیشو و بازارچه مرزی ارومیه گوشه&zwnj;ای از جاذبه&zwnj;های زیبای استان آذربایجان غربی است.\', \'آذربایجان غربی\', \'\', \'37.54261694427093\', \'45.085680823033954\', NULL, NULL),
            (3, 244, \'اردبیل\', \'استان اردبیل یکی از استان&zwnj;های سرسبز ایران است که زیباترین جاذبه&zwnj;های طبیعی ایران را در خود جای داده است. جالب است بدانید که در تابستان سال 1396 نزدیک به 9 میلیون گردشگر به این استان سفر کرده&zwnj;اند. بازار سنتی اردبیل که خوردنی&zwnj;های خوشمزه&zwnj;ای دارد، جاده بی&zwnj;نظیر اسالم به خلخال که رؤیایی&zwnj;ترین جاده جنگلی ایران است، دریاچه شورابیل اردبیل که در شهر واقع است، پل معلق پیر تقی اردبیل، پل معلق مشگین&zwnj;شهر، جنگل زیبای فندقلو، آب گرم&zwnj;های معروف سرعین و بقعه شیخ صفی&zwnj;الدین اردبیلی از جمله جاذبه&zwnj;های زیبای این استان است.\', \'اردبیل\', \'\', \'38.24078107003157\', \'48.3057811935829\', NULL, NULL),
            (4, 244, \'اصفهان\', \'اصفهان شهری باستانی است که در مرکز جغرافیایی ایران در دل فلات ایران واقع شده است؛ در نتیجه دسترسی به این استان برای تمام گردشگران آسان است. اصفهان که در فرهنگ ایرانی به نصف جهان نیز شهرت دارد، صنایع دستی قابل وصفی داشته و همچنین جاذبه&zwnj;های گردشگری زیادی را در خود جای داده است. زاینده&zwnj;رود و پل تاریخی آن با نام سی و سه پل، میدان امام، کاخ هشت بهشت که زیباترین کاخ دنیا در دوره صفویه بوده است، روستای ابیانه، کاخ عالی قاپو که یکی از شاهکارهای معماری است و در میدان نقش جهان واقع است، کویر ورزنه که لذت لمس رمل&zwnj;های طلایی و دیدن ستارگان در شب را مهیا می&zwnj;سازد، اولین تونل آکواریوم ایران، موزه موسیقی اصفهان که بسیار مورد توجه گردشگران و به خصوص گردشگران خارجی قرار گرفته است و مسجد امام که تنها گوشه&zwnj;ای از جاذبه&zwnj;های بسیار زیاد استان اصفهان است. کثرت جاذبه&zwnj;های اصفهان نه در این کلام می&zwnj;گنجد و نه در یک مسافرت چند روزه.\', \'اصفهان\', \'\', \'32.649484644384145\', \'51.67216130713064\', NULL, NULL),
            (5, 244, \'البرز\', \'استان البرز یکی از استان&zwnj;های جدید ایران است که به همین علت کمتر مورد توجه گردشگران قرار گرفته است؛ در حالی که جاذبه&zwnj;های طبیعی و تاریخی زیادی را در خود جای داده است. نوستالژیک&zwnj;ترین جاده ایران به نام جاده چالوس، روستاهای گچسر و شهرستانک که در جاده چالوس واقع شده&zwnj;اند، پیست بین&zwnj;المللی دیزین که لذت سفر در زمستان را دوچندان می&zwnj;کند، غار یخ مراد گچسر، آبشار کرکبود طالقان، امامزاده سلیمان، امامزاده زید و رحمان و کاروانسرای شاه عباسی از جمله جاذبه&zwnj;های زیبای استان البرز است.\', \'البرز\', \'\', \'35.83200194117667\', \'50.94706365088064\', NULL, NULL),
            (6, 244, \'ایلام\', \'استان ایلام یکی از استان&zwnj;های کوهستانی ایران است که میان کوه&zwnj;ها و ارتفاعات جنگلی واقع شده است. این استان، یکی از استان&zwnj;هایی است که از لحاظ جاذبه&zwnj;های گردشگری طبیعی و تاریخی بسیار غنی است؛ بطوریکه به یکی از قطب&zwnj;های گردشگری ایران تبدیل شده است. در این استان، ترکیب کوهستان و جنگل نظر گردشگران را به خود جلب می&zwnj;نماید. جاذبه&zwnj;هایی چون غار روباز زینه&zwnj;گان که معروف به باغ بهشت که زیبایی منحصر به فردی دارد، منطقه&zwnj;ی ییلاقی میشخاص با آب و هوای معتدل و کوهستانی، تنگه&zwnj;ی رازیانه ایلام که در ارتفاعات کبیرکوه قرار دارد و در فهرست آثار ملی ایران ثبت شده است، سنگ نوشته&zwnj;ی آشوری معروف به گل&zwnj;گل که خطوط میخی نقش بسته بر روی آن نشان از قدمت بالای آن دارد، گورستان باستانی چنار با قدمت قبل از میلاد، رودخانه&zwnj;ی سیمره که از تلاقی دو رودخانه&zwnj;ی قره&zwnj;سو و گاماسیاب بوجود آمده است، که فقط بخشی از جاذبه&zwnj;های زیبای این استان است.\', \'ایلام\', \'\', \'33.632346522536444\', \'46.41568876365045\', NULL, NULL),
            (7, 244, \'بوشهر\', \'استان بوشهر یکی از استان&zwnj;های ساحلی ایران است که از موقعیت استراتژیک بالایی برخوردار است. این استان از لحاظ دریایی و تاریخی مورد توجه گردشگران قرار گرفته است. سواحل زیبای استان بوشهر، موزه زئیسعلی دلواری که ساختمان آن محل زندگی این شخصیت بوده است، قلعه&zwnj;ی نصوری که به قلعه&zwnj;ی شیخ نیز معروف است و یکی از جاذبه&zwnj;های تاریخی است، کشتی رافائل که توسط هواپیماهای عراقی در بوشهر زمین&zwnj;گیر شد، بندر باستانی سی&zwnj;نیز که به شی&zwnj;نیز نیز شهرت دارد، منطقه قشلاقی شکار ممنوع کوه سیاه، گنبد نمکی جاشک که یکی از بزرگترین گنبدهای نمکی خاورمیانه است، موزه&zwnj;ی دریانوردی خلیج فارس، اینها بخشی از جاذبه&zwnj;های زیبای استان بوشهر هستند.\', \'بوشهر\', \'\', \'28.913205923241424\', \'50.82427466196259\', NULL, NULL),
            (8, 244, \'تهران\', \'استان تهران به عنوان پایتخت ایران، بخش زیادی از جاذبه&zwnj;های گردشگری را به خود اختصاص داده است و همواره مورد توجه گردشگران خارجی نیز قرار می&zwnj;گیرد. در پایتخت ایران، انواع مختلف جاذبه&zwnj;های گردشگری چون مذهبی، تاریخی، تفریحی، طبیعت و غیره را می&zwnj;توان یافت. برج میلاد که بلندترین برج ایران و ششمین برج مخابراتی جهان است و بر فراز آن می&zwnj;توان کل تهران را به نظاره نشست، کاخ نیاوران که قبلاً خانه پهلوی بوده است، موزه ارتباطات تهران که در آن می&zwnj;توان تارخ ارتباط انسان&zwnj;ها را مشاهده کرد، مجموعه کاخ سعدآباد، خزانه جواهرات ملی ایران، اولین موزه ساعت ایران، کاخ گلستان، مجموعه فرهنگی تاریخی نیاوران، تنگه واشی فیروزکوه، پل طبیعت، مجموعه تفریحی توچال، که فقط بخش اندکی از جاذبه&zwnj;های گردشگری استان پرجمعیت تهران را تشکیل می&zwnj;دهد. به جرأت می&zwnj;توان گفت که جاذبه&zwnj;های تهران هیچ وقت تمامی ندارند.\', \'تهران\', \'\', \'35.682262808065346\', \'51.3869480402783\', NULL, NULL),
            (9, 244, \'چهارمحال وبختیاری\', \'استان چهارمحال و بختیاری یکی از استان&zwnj;های کوهستانی ایران است که جاذبه&zwnj;های طبیعی زیادی را در خود جای داده و به سوئیس ایران شهرت یافته است. جاذبه&zwnj;های این استان به صورت ترکیبی از کوهستان و جنگل و آبشار قابل مشاهده است. آبشار دره عشق که آب آن با سرعت بالایی از دل کوه&zwnj;ها بیرون می&zwnj;جهد، طولانی&zwnj;ترین آبشار ایران به نام آبشار آتشگاه لردگان، چشمه دیمه که طبیعت این استان را به رخ گردشگران می&zwnj;کشد، پل زمان خان شهرکرد، دره&zwnj;ی عشق که تصویر زیبایی از طبیعت استان چهارمحال و بختیاری است، پیست اسکی چلگرد که یکی از مراکز اسکی ایران به شمار می&zwnj;رود، اینها بخشی از جاذبه&zwnj;های گردشگری استان چهارمحال و بختیاری را شامل می&zwnj;شوند.\', \'چهارمحال وبختیاری\', \'\', \'32.32427607762191\', \'50.87565251806814\', NULL, NULL),
            (10, 244, \'خراسان جنوبی\', \'استان خراسان جنوبی به عنوان یک استان همیشه بهار در تمام ایام سال مورد توجه گردشگران قرار می&zwnj;گیرد. استانی که محصولات کشاورزی چون زعفران، زرشک و عناب به شهرت آن افزوده است. این استان که در ناحیه&zwnj;ای کویری واقع شده است، زیبایی&zwnj;های طبیعی و مناطق سرسبز آن همگان را متعجب ساخته است. چشمه مرتضی علی که جوششی از آب&zwnj;های سرد و گرم است یکی از زیبایی&zwnj;های طبیعی این استان است. در این استان می&zwnj;توان ترکیبی از جاذبه&zwnj;های متنوع را یافت. عمارت کلاه فرنگی بیرجند، دره&zwnj;ی اسرارآمیز طبس معروف به کال جنی، باغ گلشن طبس که یکی از باغ&zwnj;های بزرگ ایران است، مردمان کوتاه قد یا همان لی&zwnj;لی&zwnj;پوت که در روستای ماخونیک زندگی می&zwnj;کنند بطوریکه مشابه این روستا در هیچ جای دنیا وجود ندارد، مقبره برادر امام هشتم به نام امامزاده حسین بن موسی الکاظم (ع)، سرزمین گل&zwnj;های نرگس واقع در خوسف، شهر تاریخی تون واقع در شهرستان فردوس، آبگرم معدنی فردوس، که جرئی از جاذبه&zwnj;های گردشگری استان خراسان جنوبی هستند.\', \'خراسان جنوبی\', \'\', \'32.8624073489918\', \'59.233850996886076\', NULL, NULL),
            (11, 244, \'خراسان رضوی\', \'استان خراسان رضوی پایتخت فرهنگی ایران نام گرفته است و وجود حرم مطهر امام رضا (ع) معنویت خاصی به این استان و حتی به کشور بخشیده و هر سال گردشگران خارجی زیادی را جذب می&zwnj;کند. با این حال، این استان از حیث سایر جاذبه&zwnj;های گردشگری اعم از طبیعی، تاریخی، تفریحی و غیره نیز غنی است. حمام مهدی قلی&zwnj;بیک که با نام موزه مردم شناسی مشهد شهرت دارد و معماری منحصر به فردی را به نمایش گذاشته است، اولین پارک علمی ایران با نام پارک علمی پروفسور بازیما، بزرگترین چرخ و فلک خاورمیانه که در شهر بازی پارک ملت مشهد قرار دارد، سرزمین موج&zwnj;های آبی مشهد که یکی از بزرگترین پارک&zwnj;های آبی خاورمیانه است، اولین مسجد چوبی ایران که در دهکده&zwnj;ی چوبی شهرستان نیشابور واقع است، هارونیه مشهد که یکی از جاذبه&zwnj;های تاریخی و افسانه&zwnj;ای ایران است، موزه&zwnj;ی مرکزی آستان قدس رضوی که مهمترین موزه این آستان مقدس است، آرامگاه عطار و همچنین خیام نیشابوری واقع در شهر نیشابور، آرامگاه کمال&zwnj;الملک که مشهورترین نقاش ایران بود و در نیشابور واقع شده است، آرامگاه فردوسی که در توس قرار دارد، طرقبه و شاندیز مشهد که جزو مناطق بکر طبیعی استان است، بزرگترین و قدیمی&zwnj;ترین قنات جهان با نام قنات قصبه گناباد که در شهرستان گناباد قرار دارد، اینها تنها بخشی از جاذبه&zwnj;های گردگشری استان خراسان رضوی است که مورد استقبال خیل عظیمی از گردشگران داخلی و خارجی و در تمام ایام سال قرار گرفته است.\', \'مشهد مقدس\', \'\', \'36.25349356970419\', \'59.62422152852605\', NULL, NULL),
            (12, 244, \'خراسان شمالی\', \'استان خراسان شمالی به دلیل قرار گرفتن در مسیر تردد مسافران و زائران مشهد مقدس، و همچنین به دلیل برخورداری از طبیعت زیبا، همواره مورد توجه گردشگران قرار گرفته است. برخی از جاذبه&zwnj;های گردشگری این استان عبارتند از: تفرجگاه تاریخی و طبیعی بش&zwnj;قارداش، یکی از زیباترین و قدیمی&zwnj;ترین پارک&zwnj;های کشور که در منطقه&zwnj;ای به نام بابا امان واقع است، ارگ و شهر تاریخی بلقیس با قدمتی حدود چهار هزار سال، آتشکده اسپاخو، روستای گردشگری روئین در شهرستان اسفراین، روستای گردشگری اسطرخی که با عنوان بهشت گمشده شیروان شهرت یافته است، دژ آلمادوشن و بسیاری از جاذبه&zwnj;های دیگر که در این بحث نمی&zwnj;گنجد.\', \'خراسان شمالی\', \'\', \'37.463244501101414\', \'57.32022934722136\', NULL, NULL),
            (13, 244, \'خوزستان\', \'استان خوزستان یکی از معدود استان&zwnj;های ایران است که در آن می&zwnj;توان تنوع قومی و مذهبی را مشاهده کرد. وجود آثار تاریخی و همچنین طبیعت زیبا و متنوع این است، توجه گردشگران بسیاری را به خود جلب نموده است. تعدادی از پیروان حضرت یحیی که با نام صائبی&zwnj;ها یا مندایی&zwnj;ها شهرت یافته&zwnj;اند در اهواز زندگی می&zwnj;کنند و شاید دیدن آداب و رسوم آنها برای شما جالب باشد. آبادان یکی دیگر از شهرهای این استان است که قبلاً یکی از زیباترین شهرهای ایران لقب گرفته بود. کنیسه یهودیان، کلیسای ارامنه، قدمگاه خضر نبی و زیارتگاه سیدعباس جزو جاذبه&zwnj;های دیدنی آبادان است. پل معلق اهواز که بر روی رودخانه کارون احداث شده است، معماری&zwnj;های زیبای شهر دزفول که اوج آن را می&zwnj;توان در خانه تیزنو مشاهده کرد، پل ساسانی دزفول، مسجد جامع دزفول، یکی از زیباترین آبشارهای ایران که با نام آبشار شوی در دزفول واقع است، قدیمی&zwnj;ترین نرگس&zwnj;زار ایران که در شهرستان بهبهان واقع است، قلعه شوش، یکی از بناهای تاریخی و بزرگ خاورمیانه به نام معبد بزرگ زیگورات چغازنبیل در شوش واقع است، آرامگاه دانیال نبی که هر ساله مورد استقبال یهودیان ایران قرار می&zwnj;گیرد، روستای زیبای لیوس که یکی از روستاهای هدف گردشگری است و در شهرستان دزفول واقع است، تنها بخشی از جاذبه&zwnj;های زیبای استان خوزستان می&zwnj;باشند.\', \'خوزستان\', \'\', \'31.31239287698394\', \'48.6754301422684\', NULL, NULL),
            (14, 244, \'زنجان\', \'استان زنجان یکی از استان&zwnj;های هدف گردشگری است که جاذبه&zwnj;های گردشگری متنوعی را در خود جای داده است. جاذبه&zwnj;هایی چون بازار قدیمی زنجان که قدمت آن به دوره قاجار می&zwnj;رسد، جاذبه&zwnj;ی تاریخی رختشوی خانه واقع در بافت قدیمی زنجان، پدیده&zwnj;ی طبیعی زیبای هودو معروف به دودکش جن، حسینیه اعظم زنجان که در ایام عزاداری از سیل عظیمی از عزاداران استتقبال می&zwnj;کند، بزرگترین گنبد آجری جهان با نام گنبد سلطانیه، کوه&zwnj;های زیبا و رنگین&zwnj;کمانی آلاداغ لار، قلعه لک&zwnj;لک&zwnj;ها، آبشار شارشار که در شمال شهر زنجان واقع شده است، روستای گردشگری شیلاندر، از جمله آثار گردشگری استان زنجان هستند.\', \'زنجان\', \'\', \'36.67738841320717\', \'48.50970792075543\', NULL, NULL),
            (15, 244, \'سمنان\', \'طبیعت بکر، زیبا و دست&zwnj;نخورده&zwnj;ی استان سمنان که تلفیقی از کویر و جنگل است، همواره نظر گردشگران را به خود جلب نموده است. چشمه&zwnj;های معدنی آبگرم سمنان که خاصیت درمانی دارند. آستان مقدس پیغمبران که مقبره فرزندان حضرت نوع (ع) به نام&zwnj;های سام و لام در سمنان واقع است. رباط سنگی انوشیروای و کاروانسرای شاه عباسی جزو جاذبه&zwnj;های تاریخی آهوان از توابع این استان است. آرامگاه بایزید بسطامی از عارفان بزرگ ایران در شهر بسطام قرار دارد. یکی از جنگل&zwnj;های هیرکانی ایران به نام جنگل ابر در شهرستان خوش آب و هوای شاهرود واقع شده است. گونه&zwnj;های جانوری و گیاهی متنوعی را می&zwnj;توان در جنگل&zwnj;های اولنگ، دشت&zwnj;شاد و نام&zwnj;نیک در شهرستان شاهرود یافت. زیستگاه توران که زیستگاه نادرترین گونه&zwnj;های جانوری مانند یوزپلنگ آسیایی، گورخر ایرانی و پرندگانی چون زاغ بور و هوبره می&zwnj;باشد. منطقه خوش آب و هوای خوش&zwnj;ییلاق، نیز زیستگاه گونه&zwnj;های گیاهی و جانوری متنوعی است. چشمه هفت رنگ که با تنوع رنگ آبی که دارد از خواص درمانی نیز برخوردار است. این موارد تنها بخش اندکی از جاذبه&zwnj;های گردشگری استان سمنان می&zwnj;باشند.\', \'سمنان\', \'\', \'35.57418780789907\', \'53.382580575525594\', NULL, NULL),
            (16, 244, \'سیستان وبلوچستان\', \'استان سیستان و بلوچستان اگرچه یکی از محروم&zwnj;ترین استان&zwnj;های کشور از لحاظ توسعه می&zwnj;باشد؛ ولی از جاذبه&zwnj;های گردشگری و تاریخی زیادی برخوردار است که کمتر مورد توجه گردشگران قرار گرفته است. این استان به باور بعضی یازدهمین سرزمینی است که توسط اهورامزدا آفریده شده است. سواحل این استان که کشتی&zwnj;های بزرگ اقیانوس&zwnj;پیما در آن لنگر می&zwnj;اندازند در چابهار واقع شده است. چابهار که در همه فصل&zwnj;های آن می&zwnj;توان آب و هوای بهاری را مشاهده کرد، در فصل زمستان گرم&zwnj;ترین نقطه&zwnj;ی ایران و در فصل تابستان خنک&zwnj;ترین بندر جنوبی کشور است. جاذبه&zwnj;ی بولوبولو که به ناف دریا هم معروف است در چابهار واقع شده است. شهر باستانی پنج هزار ساله که قدمت آن به قبل از میلاد مسیح می&zwnj;رسد، ساحل سخره&zwnj;ای، قبرستان جن&zwnj;ها، غارهای سه&zwnj;گانه، شهر نیشکر، شهر قدیمی قصرقند، دریاچه هامون که سومین دریاچه بزرگ ایران است، سواحل زیبای چابهار که قابلیت موج&zwnj;سواری دارد، اینها فقط بخشی از استان پهناور سیستان و بلوچستان است.\', \'سیستان وبلوچستان\', \'\', \'29.445856202599096\', \'60.89120047314236\', NULL, NULL),
            (17, 244, \'فارس\', \'استان فارس یکی از استان&zwnj;های مهم گردشگری ایران است که تنوعی از همه&zwnj;ی جاذبه&zwnj;ها را می&zwnj;توان در آن یافت؛ به همین دلیل بسیار مورد توجه گردشگران داخلی و خارجی قرار گرفته است. مرکز این استان، شهر شیراز که به شهر بهارنارنج نیز معروف است. برخی از مهم&zwnj;ترین آثار تاریخی جهان را می&zwnj;توان در این استان یافت. یکی از این جاذبه&zwnj;ها، تخت جمشید است که عظمت امپراتوری هخامنشی در آن نمایان است. آرامگاه احمدبن موسی کاظم (ع) فرزند بزرگ امام موسی کاظم (ع) و معروف به شاهچراغ که مشهورترین جاذبه فرهنگی مذهبی این استان است. ارگ کریم&zwnj;هان زند و مسجد وکیل که از یادگاران دوران زندیه است، قدیمی&zwnj;ترین مسجد شیراز با نام مسجد جامع عتیق، حمام وکیل شیراز، حافظیه که آرامگاه حافظ شاعر نام&zwnj;آشنای ایران است، دروازه قرآن، باغ ارم که شاهکار معماری ایرانی است و در فهرست میراث جهانی یونسکو نیز ثبت شده است، آرامگاه کوروش کبیر، مسجد نصیرالملک، تنها بخشی از جاذبه&zwnj;های تاریخی استان فارس است. از جمله جاذبه&zwnj;های طبیعی این استان نیز می&zwnj;توان از آبشار فدامی داراب که یکی از جاذبه&zwnj;های زیبای ایران است، تنگه هایقر فیروزآباد که در ارتفاعات زاگرس واقع است، عروس تنگه&zwnj;های ایران به نام تنگه&zwnj;ی رغز داراب که در شهرستان داراب واقع است، آبشار مارگون سپیدان که به بهشت آبشارهای ایران معروف گشته است، نام برد. ذکر اکثر جاذبه&zwnj;های گردشگری استان فارس در این گفتار نمی&zwnj;گنجد. اگر قصد سفر به این استان دارید حتماً برای تمام ثانیه&zwnj;های سفرتان برنامه&zwnj;ریزی کنید.\', \'فارس\', \'\', \'29.588871621929485\', \'52.58956477102208\', NULL, NULL),
            (18, 244, \'قزوین\', \'استان قزوین در دامنه رشته&zwnj;کوه&zwnj;های البرز واقع شده و یکی از استان&zwnj;های کوهستانی ایران است که بسیار مورد توجه کوهنوردان نیز قرار گرفته است. هم جاذبه&zwnj;های طبیعی این استان و هم جاذبه&zwnj;های تاریخی آن، نظر گردشگران را به خود جلب نموده است. برخی از جاذبه&zwnj;های تاریخی این استان عبارتند از: کاروانسرای سعدالسلطنه که از بناهای دوران قاجاریه است، امامزاده شاهزاده حسین (ع) که معروف&zwnj;ترین جاذبه&zwnj;ی فرهنگی شهر قزوین است، کاخ چهل&zwnj;ستون قزوین که از یادگاران دوران صفویه است، کوچک&zwnj;ترین کلیسای ایران با نام کلیسای کانتور، حسینیه امینی&zwnj;های قزوین که یادگار دوران قاجاریه است و هنر و معماری آن دوران را به تصویر می&zwnj;کشد، قدیمی&zwnj;ترین مسجد جامع ایران، بازار قزوین که یکی از شاهکارهای معماری ایرانی است. از جمله جاذبه&zwnj;های طبیعی قزوین نیز می&zwnj;توان دریاچه اوان&zwnj;الموت، چشم&zwnj;انداز زیبای رودخانه ارنزک، آبگرم یله گنبد، ایوان سنگی نیاق و غار یخی انگول را نام برد.\', \'قزوین\', \'\', \'36.267243756460324\', \'49.99856603378112\', NULL, NULL),
            (19, 244, \'قم\', \'استان قم یکی دیگر از استان&zwnj;های مذهبی ایران است که سالانه 20 میلیون نفر به آن سفر می&zwnj;کنند. مرقد مطهر حضرت معصومه (س) و مسجد مقدس جمکران به معنویت این استان افزوده است. علیرغم اینکه استان قم در یک منطقه کویری واقع شده است ولی از جاذبه&zwnj;های طبیعی زیادی نیز برخوردار است. منطقه پلنگ&zwnj;دره که در دره&zwnj;ی ریواس&zwnj;ها واقع شده است یکی از جاذبه&zwnj;های طبیعی این استان است. دریاچه ساوه یا شاهی قم و یا حوض سلطان تالابی نیز یکی دیگر از این جاذبه&zwnj;هاست. تالاب بهشت معصومیه که برخی از گونه&zwnj;های پرندگان را می&zwnj;توان در آن یافت. منطقه ییلاقی خلجستان که در دره&zwnj;های دهستان قاهان قرار دارد، روستای سرسبز جمیزقان، سد تاریخی کبار قم، بخشی از جاذبه&zwnj;های گردشگری استان قم هستند.\', \'قم\', \'\', \'34.6320196506774\', \'50.884332099203675\', NULL, NULL),
            (20, 244, \'کردستان\', \'استان کردستان یکی از استان&zwnj;های تاریخی ایران است که به دلیل قدمت طولانی آن، جاذبه&zwnj;های تاریخی بسیاری را می&zwnj;توان در آن یافت. این استان به دلیل جغرافیای منحصر به فردی که دارد، از جاذبه&zwnj;های طبیعی بسیاری نیز برخودار است و یکی از زیباترین مناظر گردشگری ایران را در دامنه رشته&zwnj;کوه&zwnj;های زاگرس رقم شده است. تخت سلیمان تکاب که یکی از معروف&zwnj;ترین جاذبه&zwnj;های تاریخی ایران است که گوشه&zwnj;ای از تمدن ایران در آن به چشم می&zwnj;خورد و در فهرست آثار تاریخی جهان نیز ثبت شده است. روستاهای هویه، پالنگان، هورامان، تنگی سر سنندج جزو جاذبه&zwnj;های زیبای گردشگری و روستاهای هدف گردشگری این استان هستند. یکی از بزرگترین دریاچه&zwnj;های آب شیرین ایران به نام دریاچه زریوار مریوان در این استان قرار دارد. غار کرفتو که اقامتگاه مردمان قدیم است. جنگل&zwnj;های بلوط، مجموعه پارک تفریحی آبیدر، دریاچه سد قشلاق، قلعه&zwnj;های باستانی، چهل چشمه، چشمه باباگرگر و چشمه آب تلخ که آب آن در درمان بیماری&zwnj;های روماتیسمی بکار می&zwnj;رود، بخشی از این جاذبه&zwnj;ها هستند.\', \'کردستان\', \'\', \'35.31495389780103\', \'46.993480880744755\', NULL, NULL),
            (21, 244, \'کرمان\', \'استان کرمان یکی از استان&zwnj;های مهم و تاریخی ایران با جاذبه&zwnj;های گردشگری بسیاری است؛ به طوریکه حدود 700 اثر ثبت شده در فهرست آثار ملی ایران دارد. یکی از آثار گردشکری این استان، کلوت شهداد کرمان است که در گرم&zwnj;ترین نقطه زمین قرار دارد. روستای میمند شهر بابک که یکی از روستاهای صخره&zwnj;ای ایران است که خانه&zwnj;های آن در دل کوه و از کندن صخره&zwnj;ها ساخته شده است، ارگ تاریخی راین کرمان، ارگ بم که قدیمی&zwnj;ترین و بزرگترین بنای خشتی جهان بشمار می&zwnj;رود و در زلزله کرمان کمی تخریب شده است، باغ شاهزاده ماهان که قدمت آن به عصر قاجار می&zwnj;رسد، حمام گنجعلی خان که معماری دوران صفویه را نشان می&zwnj;دهد، تنها بنای سنگی کرمان به نام گنبد جبلیه که قبل از اسلام ساخته شده و به گنبد گبری نیز معروف است، باغ سنگی سیرجان که یکی از جاذبه&zwnj;های نادر است، آبشار راین که در دامنه&zwnj;های کوه هراز واقع شده است، کوه هزار بلندترین قله کرمان و سومین قله بلند ایران است، ارگ خشتی راین که بعد از ارگ بم دومین بنای خشتی بزرگ جهان است، بازار بزرگ کرمان، مسجد ملک، یخدان&zwnj;های کرمان، آتشکده و موزه زرتشتیان، گنبد مشتاقیه که از آثار عصر قاجاریه است، آثار و بقایای دایناسورها را می&zwnj;توان در شمال کرمان دید، آرامگاه شاه نعمت&zwnj;الله در شهر ماهان قرار دارد. این جاذبه&zwnj;ها فقط بخش اندکی از جاذبه&zwnj;های گردشگری فراوان استان کرمان است.\', \'کرمان\', \'\', \'30.272912901155912\', \'57.09391423681814\', NULL, NULL),
            (22, 244, \'کرمانشاه\', \'استان کرمانشاه یکی از پرجاذبه&zwnj;ترین مناطق ایران و شهرکرمانشاه یکی از قدیمی&zwnj;ترین شهرهای ایران است. به دلیل همجواری این استان با کشور عراق، همواره گردشگران زیادی از عراق نیز به این استان سفر می&zwnj;کنند. این استان حدود 700 اثر تاریخی ثبت شده در فهرست آثار ملی ایران دارد. از جمله آثار تاریخی این استان می&zwnj;توان بیستون را نام برد که ثبت جهانی شده است. معبد آناهیتا که در بالای دشت کنگاور واقع شده است از دیگر آثار تاریخی این استان است. جاذبه&zwnj;های دیگری چون طاق بستان کرمانشاه که از شاهکارهای معماری دوران ساسانی است، شهر زیبای صحنه که به پایتخت موسیقی عرفانی ایران شهرت یافته است، مجسمه سنگی هرکول که در بیستون قرار دارد، کاروانسرای شاه عباسی که در دامنه کوه بیستون واقع شده است، غار پرآور کرمانشاه، روستای زیبای هجیج که از روستاهای هدف گردشگری این استان است، آبشار پیران و بسیاری از جاذبه&zwnj;های دیگر که در این بحث نمی&zwnj;گنجد. علاوه بر اینها، سوغات کرمانشاه مانند نان برنجی، نان خرمایی و شیرینی&zwnj;ها و غذاهای معروف کرمانشاه نیز نظر بسیاری از علاقه&zwnj;مندان را به خود جلب نموده است.\', \'کرمانشاه\', \'\', \'34.32028879302098\', \'47.080426805337765\', NULL, NULL),
            (23, 244, \'کهگیلویه وبویراحمد\', \'استان کهگیلویه و بویر احمد به سرزمین چهار فصل با دو نوع آب و هوای سردسیر و گرمسیر شهرت یافته است. سرزمینی که در دل رشته&zwnj;کوه&zwnj;های زاگراس قرار دارد. مرکز این استان شهر یاسوج است که به دلیل اینکه جاذبه&zwnj;های طبیعی بسیاری که در آن واقع شده است به پایتخت طبیعت ایران شهرت یافته است. آبشار یاسوج، تنگ گنجه&zwnj;ای که در دل کوه دنا واقع شده است، بزرگ&zwnj;ترین پارک جنگلی جنوب کشور به نام پارک جنگلی یاسوج، پیست اسکی کاکان یاسوج که همواره مورد توجه علاقه&zwnj;مندان به ورزش اسکی قرار می&zwnj;گیرد، شهر سی&zwnj;سخت که آرامگاه سی پهلوان قرار گرفته است، آرامگاه بی&zwnj;بی حکیمه خواهر (س) خواهر امام هشتم (ع) که در اطراف گچساران واقع است، در شهرستان بهمئی می&zwnj;توان نقوش برجسته تنگ سروک را مشاهده کرد که قدمت آن به دوره اشکانیان می&zwnj;رسد، دریاچه کوه گل سی&zwnj;سخت که نگین این استان است، روستای گردشگری کریک یاسوج، هفتمین آبشار بلند ایران به نام آبشار کمردوغ کهکیلویه و بویر احمد، تنها بخشی از جاذبه&zwnj;های گردشگری این استان زیباست.\', \'کهگیلویه وبویراحمد\', \'\', \'30.666233314227394\', \'51.594856717839775\', NULL, NULL),
            (24, 244, \'گلستان\', \'گلستان یکی از استان&zwnj;های ایران است که در آن اقوام متعددی چون ترکمن&zwnj;ها، فارس&zwnj;ها، سیستانی&zwnj;ها، بلوچ&zwnj;ها، ترک&zwnj;ها، قزاق&zwnj;ها و غیره زندگی می&zwnj;کنند. استان گلستان یکی از استان&zwnj;های تاریخی ایران نیز بشمار می&zwnj;رود. این استان به دلیل زیبایی&zwnj;های طبیعی بسیاری که دارد همواره مورد توجه گردشگران قرار گرفته است. امکان ندارد که گردشگری به این استان سفر کند ولی از نهارخوران گرگان که شهرت زیادی دارد غفلت کند. در انتهای جاده نهارخوران به روستایی بسیار زیبا به نام زیارت می&zwnj;رسید که از جاذبه&zwnj;های طبیعی منحصر به فرد ایران است. یکی از جاذبه&zwnj;های تاریخی استان گلستان، دیوار بزرگ گرگان نام دارد که بزرگترین سد دفاعی دنیا را به خود اختصاص داده است. پارک جنگلی النگ&zwnj;دره یکی دیگر از این جاذبه&zwnj;هاست که در فصل پاییز، شکوه طبیعت پاییزی گرگان را به نمایش می&zwnj;گذارد. جاذبه&zwnj;های دیگری چون دهکده جهان&zwnj;نما، آبشارهای زیبای شیرآباد گلستان، دریاچه توشن، چشمه گل رامیان، آبشار کبودوال علی آباد کتول که بزرگترین آبشار تمام خزه&zwnj;ای دنیاست، نخستین پارک ملی ایران به نام پارک ملی گلستان که گونه&zwnj;های گیاهی و جانوری متنوعی را می&zwnj;توان در آن مشاهده کرد، آبشار آق سو در پارک ملی گلستان، پارک&zwnj;های جنگلی دلند، کردکوی و قرق، مجموعه آبشارهای لوه در شهر گالیکش شهرستان مینودشت، آبشارهای زیارت، رنگو، شیرآباد و لاشو. آبشار هفت طبقه شادان در شهرستان کردکوی که تنها بخشی از جاذبه&zwnj;های طبیعی این استان است و شاید گفت در هیچ استانی این همه جاذبه&zwnj;ی طبیعی وجود ندارد. از جمله جاذبه&zwnj;های تاریخی استان گلستان نیز می&zwnj;توان میل گنبد در شهر گنبد کاووس را نام برد که از معماری ویژه&zwnj;ای برخوردار است. مدرسه عمادیه که قدمت آن به دوران صفویه می&zwnj;رسد یکی دیگر از این جاذبه&zwnj;هاست. موزه گرگان که در کاخ سلطنتی پارک شهر واقع است نیز نظر گردشگران را به خود جلب نموده است. کوه&zwnj;های استان گلستان، غارهایی چون کیارام، شیرآباد، پشمکی، حمام خدایی، سم، گرو یا گرآو، روستاهای هدف گردشگری چون روستای جهان&zwnj;نما و بندر ترکمن و بندر گز و اسکله&zwnj;های این بنادر و بسیاری از موارد دیگر که در این مطلب نمی&zwnj;گنجد.\', \'گلستان\', \'\', \'36.84190107533155\', \'54.44550735849862\', NULL, NULL),
            (25, 244, \'گیلان\', \'استان گیلان یکی از استان&zwnj;های شمالی ایران است که سرسبزی آن نظر بسیاری از گردشگران را به خود جلب نموده است. این استان مرکز دو قوم باستانی ایران به نام&zwnj;های گیل و دیلم است. استان گیلان در اواخر دوره قاجاریه آنقدر پیشرفت کرد که به دروازه اروپا شناخته شد. این استان به دلیل اینکه در سواحل جنوبی دریای خزر واقع شده است همواره مورد توجه گردشگران قرار گرفته است. از جمله جاذبه&zwnj;های گردشگری این استان می&zwnj;توان جاده گردشگری و زیبای خلخال به اسالم را نام برد که زیباترین جاده جنگلی ایران نام گرفته است. اولین ایستگاه پرندگان مهاجر در ایران در تالاب استیل آستارا قرار دارد که همواره مورد توجه قرار گرفته است. بندر انزلی که اولین و بزرگترین بندر ایران است در این استان قرار دارد. شهر انزلی نیز یکی از مهمترین شهرهای گردشگری ایران و از مراکز مهم اقتصادی ایران است که پر باران&zwnj;ترین شهر ایران نیز نام گرفته است. دیگر جاذبه&zwnj;های گردشگری این استان عبارتند از: سواحل زیبای دریاچه خزر مانند ساحل چمخاله، گردنه حیران که یکی از معروف&zwnj;ترین گردنه&zwnj;های ایران است، پارک جنگلی گیسوم، قلعه رودخان گیلان که در دامنه کوه&zwnj;های جنگلی واقع است، دریاچه سقالکسار گیلان که از دریاچه&zwnj;های پاک ایران بشمار می&zwnj;رود، شهر بسیار زیبای ماسوله، روستاهای جواهر دشت رودسر، امامزاده ابراهیم و اولسبلنگاه ماسال که از جمله روستاهای هدف گردشگری هستند، آبشارهایی چون آبشار دودوزن خرمکش، ویسادار رضوانشهر، لونک، لاتون گیلان که بلندترین آبشار ایران است، شیطان کوه، دریاچه بره&zwnj;سر، منطقه دیلمان که در ارتفاعات گیلان واقع است، شالیزارهای گیلان و آداب و رسوم برداشت برنج، تالاب گیلان که خود یکی از شاهکارهای طبیعت این استان است، تنها بخش اندکی از جاذبه&zwnj;های طبیعی بسیار این استان سرسبز است. همچنین، میدان زیبای شهرداری رشت که یکی از میدان&zwnj;های تاریخی ایران است با معماری زیبایی که دارد مورد توجه گردشگران قرار گرفته است.\', \'گیلان\', \'\', \'37.25936993734272\', \'49.59183880012415\', NULL, NULL),
            (26, 244, \'لرستان\', \'استان لرستان یکی از استان&zwnj;های غربی ایران با آب و هوای معتدل و کوهستانی است که در رشته&zwnj;کوه&zwnj;های زاگرس قرار گرفته است. امکان دیدین جاذبه&zwnj;های گردشگری بسیاری چون طبیعی، تاریخی و فرهنگی در این استان فراهم است. از جمله جاذبه&zwnj;های طبیعی استان لرستان عبارتند از دریاچه گهر دورود که از جمله زیبایی&zwnj;های اشترانکوه است، آبشار بیشه لرستان، تنگه هلت پلدختر که رودخانه&zwnj;ای منور در دل رشته&zwnj;کوه&zwnj;های زاگرس است، آبشار برنجه شول آباد، آبشار نوژیان که یکی از بلندترین آبشارهای ایران است، آبشار آب سفید الیگودرز که به عروس آبشارهای ایران شهره است،دره خزینه لرستان که بلندترین پل معلق ایران نیز در آن جای دارد، دره نی&zwnj;گاه دورود،تنگه شیرز که در کوهدشت واقع است، دریاچه کیو خرم&zwnj;آباد که در دل شهر واقع است، تونل برفی ازنا که از زیبایی&zwnj;های طبیعی اشترانکوه است و لاله&zwnj;های واژگون الیگودرز. از جمله جاذبه&zwnj;های تاریخی استان لرستان نیر می&zwnj;توان قلعه فلک&zwnj;الافلاک واقع در شهر خرم&zwnj;شهر را نام برد که نشان از قدمت بسیار این شهر دارد. سنگ نبشته و گرداب سنگی خرم&zwnj;آباد، پل شاپوری که به پل شکسته نیز شهرت دارد، پل کشکان رود، مناره آجری خرم&zwnj;آباد، حمام تاریخی گپ، مقبره باباطاهر واقع در مرکز خرم&zwnj;آباد نیز از دیگر جاذبه&zwnj;های تاریخی فرهنگی این استان است.\', \'لرستان\', \'\', \'33.45062766539178\', \'48.34879704931814\', NULL, NULL),
            (27, 244, \'مازندران\', \'استان مازندران یکی از استان&zwnj;های سرسبز شمال کشور است. این استان از حیث جاذبه&zwnj;های طبیعی آنقدر غنی است که به یکی از قطب&zwnj;های گردشگری کشور تبدیل شده است. ارتباط مازندران با مرکز کشور از طریق محورهای هراز، کندوان و سوادکوه سبب شده است که سالانه پذیرای حدود 12 میلیون گردشگر داخلی و ده&zwnj;ها هزار گردشگری خارجی باشد. برخی از جاذبه&zwnj;های طبیعی استان مازندران عبارتند از: پله&zwnj;های نورانی معروف به باداب سورت با رنگ&zwnj;های متنوع همراه با آبی که گاه شور و گاه شیرین است، روستای گردشگری لاویج از توابع شهرستان نور که خود دارای جاذبه&zwnj;هایی چون آبگرم لاویج، آبشار حرام، کلیسای آنتوان مقدس و خانه نیما یوشیج است، دریاچه بسیار زیبا و دیدنی چورت معروف به خواهر خوانده دریاچه خزر، جنگل زیبای دالخانی معروف به دالان بهشت در شهرستان رامسر واقع است، یکی از بزرگترین و زیباترین سدهای ایران به نام لار در استان مازندران قرار دارد که گونه&zwnj;های متنوع گیاهی و جانوری چون فلامینگو، کبک، پلنگ و بز کوهی را می&zwnj;توان در این منطقه مشاهده کرد، جنگل&zwnj;های دوهزار که از جنگل&zwnj;های بسیار زیبای ایران است و در شهرستان تنکابن واقع شده است، آبشار آب پری یا آب رویان یکی دیگر از زیبایی&zwnj;های طبیعی این استان است و در نزدیکی آرامگاه نیما یوشیج واقع است، پل ورسک از قدیمی&zwnj;ترین پل&zwnj;های راه&zwnj;آهن ایران که در کتاب گینس ثبت شده است، دشت گل&zwnj;های بابونه زرد رنگ همراه با عطر زیبا به نام دشت دریاسر که به دشت اردیبهشت ایران نیز شهرت یافته است، بام مازندران همراه با آسمان ابرها را می&zwnj;توان در ییلاقات فیلبند مشاهده کرد، ییلاقت کلاردشت، غار آبی دانیال، تالاب میانکاله، که تنها بخش اندکی از جاذبه&zwnj;های طبیعی بسیار استان مازندران است. از دیگر جاذبه&zwnj;های این استان می&zwnj;توان به مواردی چون قلعه مارکوه رامسر، تله کابین رامسر، کاخ موزه زیبای رامسر، تله کابین و منطقه زیبای گردشگری نمک آبرود که به نام شهرک توریستی نمک آبرود معروف است، خانه و آرامگاه نیما یوشیج، اشاره نمود.\', \'مازندران\', \'\', \'36.56183150712306\', \'53.0635738000683\', NULL, NULL),
            (28, 244, \'مرکزی\', \'استان مرکزی یکی از استان&zwnj;های کوهستانی ایران با زمستان&zwnj;های سرد و تابستان&zwnj;های معتدل است. این استان جاذبه&zwnj;های گردشگری و طبیعی متنوعی دارد که برخی از این جاذبه&zwnj;هاب عبارتند از: تالاب و کویر میقان واقع در دشت فراهان، غار سفید خانی معروف به عین هو در کوه&zwnj;های سفیدخانی از توابع شهر سنجان قرار دارد، مجموعه غارهای انجدان واقع در جنوب شرقی اراک و در دامنه کوه چشمه&zwnj;بین، چشمه معدنی چپقلی که به اعتقاد مردم محل آب آن برای دفع سنگ کلیه مؤثر است، منطقه حفاظت شده هفتاد قله، چشمه فضل علی و چمن دره، کویر میقان و تالاب میقان با گونه&zwnj;های متنوع و کمیاب جانوری، باغ وحش و پارک جنگلی شهید باهنر و همچنین پارک جنگلی شهید رجایی، روستاهای هدف گردشگری چون روستای انجدان و روستای هزاوه، گورستان چشمه پهن واقع در کوهپایه&zwnj;های جنوبی کوه لنه، کاروانسرای دولت آباد، آرامگاه&zwnj;های شاه غریب و شاه قلندر از پیشوایان فرقه اسماعیلیه، بقعه چهل دختران از آثار دوره صفویه و متعلق به فرقه اسماعیلیه و بسیاری از جاذبه&zwnj;های دیگر که در این کلام نمی&zwnj;گنجد.\', \'مرکزی\', \'\', \'34.0830517227066\', \'49.70824557048991\', NULL, NULL),
            (29, 244, \'هرمزگان\', \'استان هرمزگان یکی از استان&zwnj;های ساحلی ایران است به دیلیل برخورداری از جزایر متنوع، یکی از استان&zwnj;های استراتژیک ایران نیز به شمار می&zwnj;رود. برخی از مهمترین جاذبه&zwnj;های تاریخی، مذهبی و طبیعی ایران را می&zwnj;توان در این استان مشاهده کرد. فصل&zwnj;های پاییز و زمستان از جمله فصل&zwnj;های پیشنهادی جهت سفر به این استان است. برخی از جاذبه&zwnj;های این استان عبارتند از: جزیره زیبا و توریستی کیش که همواره مورد توجه خیل عظیمی از گردشگران داخلی و خارجی قرار گرفته است، شهر زیرزمینی خلیج فارس معروف به کاریز کیش، تفریحات آبی کیش، کشتی یونانی کیش، باغ پرندگان، پارک آبی اوشن، کاریز جزیره کیش، شهر جزیره در کیش، درخت انجیر معابد کیش و هتل بزرگ داریوش، جزیره زیبای قشم با جاذبه&zwnj;های متنوعی چون تنگه چاهکوه، غار خربس، جزایر ناز، روستای لافت معروف به سرزمین بادگیرها، اولین پارک کروکودیل خاورمیانه به نام پارک کروکودیل نوپک قشم، دره ستاره&zwnj;ها، جزیره زیبای هنگام قشم، تنگه چاهکوه، مسجد برخ، مسجد توران شاه، چاه&zwnj;های لافت و جنگل&zwnj;های حرای قشم، معبد هندوها واقع در شهر بندرعباس که از نمادهای مهم این شهر محسوب می&zwnj;شود و سبب ایجاد ارتباط فرهنگی-هنری بین کشور ایران و هندوستان شده است، طولانی&zwnj;ترین پل تاریخی ایران با قدمتی 500 ساله به نام پل لاتیدان واقع در شهرستان خمیر که طول آن حدود 3 برابر سی و سه پل اصفهان است، آبشار و چشمه معدنی گنو در بندرعباس با استخرهای مردانه و زنانه مجزا که آب گوگردی آن جهت استحمام مورد توجه قرار گرفته است، مجموعه غارهای روستای خوربس (خوربز) یا خربس (خربز) در غرب شهر قشم، روستای گردشگری بناب همراه با چشمه&zwnj;ها و رودخانه&zwnj;ها و نخلستان&zwnj;های بسیار، قلعه تاریخی هزاره میناب که در بین مردم محل به قلعه بی&zwnj;بی مینو شهرت دارد، روستای گردشگری بسیار زیبای کهتویه که مشابه آن را در هیچ کجای ایران نمی&zwnj;توان یافت و بنادر متنوع استان هرمزگان.\', \'هرمزگان\', \'\', \'27.171683937517884\', \'56.2701856017178\', NULL, NULL),
            (30, 244, \'همدان\', \'همدان واقع در غرب ایران به پایتخت تاریخ و تمدن ایران شهرت یافته است که به همراه آتن یونان و رم ایتالیا از معدود شهرهای باستانی جهان است. در این استان می&zwnj;توان انواع مختلف جاذبه&zwnj;های گردشگری چون طبیعی، مذهبی و تاریخی را یافت و به همین دلیل همواره گردشگران زیادی به سلایق مختلف به این استان سفر می&zwnj;کنند. مرکز این استان که قبلاً هگمتانه نام داشت، پایتخت ایران  در زمان مادها بود. از معروف&zwnj;ترین و زیباترین جاذبه&zwnj;های استان همدان می&zwnj;توان غار علیصدر را نام برد که طولانی&zwnj;ترین غار آبی جهان است. این غار در فهرست آثار ملی ایران نیز به ثبت رسیده است. دهکده گنجنامه دیگر جاذبه تاریخی این استان است که در کوه&zwnj;های الوند واقع شده است. آثار سنگ نبشته&zwnj;های گنجنامه در این دهکده مشهود است. آبشار گنجنامه، تله کابین، پیست اسکی تاریک دره، تله سیژ، تله اسکی، پل معلق و تیرول یا همان پرواز فردی از جمله جاذبه&zwnj;های دهکده گنجنامه است. در شرق همدان می&zwnj;توان از دریاچه و سد اکباتان دیدن کرد که تجربه قایقرانی و اسکی روی آب را نیز مهیا ساخته است. آرامگاه باباطاهر عریان با قدمت بسیار بالا و معماری باشکوه از دیگر جاذبه&zwnj;های استان همدان است که در فهرست آثار ملی ایران ثبت شده است. موزه صنایع دستی استان نیز در کنار این آرامگاه واقع شده و مورد توجه قرار گرفته است. آرامگاه ابوعلی سینا، طبیب ایرانی که شهرت جهانی دارد نیز در همدان واقع است که با معماری باشکوه خود مورد توجه است. دیگر جاذبه&zwnj;های این استان عبارتند از: تپه هگمتانه، آرامگاه حیقوق نبی (ع)، بارگاه مطهر امامزاده حسین معروف به شاهزاده حسین (ع) پسر امام حسن عسگری (ع)، گنبد علویان، برج تاریخی قربان، حمام تاریخی قلعه، بازار سنتی همدان، موزه تاریخ طبیعی همدان واقع در دانشکده کشاورزی، پارک سیفیه ملایر که به سبک معماری ایتالیایی ساخته شده است، دشت میشان در دامنه کوه الوند، طبیعت زیبای سراب گیان، آبشار گنجنامه، روستای گردشگری ورکانه و بسیاری از جاذبه&zwnj;های دیگر.\', \'همدان\', \'\', \'34.800915115505916\', \'48.511288004310245\', NULL, NULL),
            (31, 244, \'یزد\', \'استان یزد یکی از استان&zwnj;های تاریخی ایران است. شهر یزد با معماری تاریخی و منحصر به فردی که دارد به عنوان یکی از زیباترین شهرهای خشتی جهان نیز شهرت یافته است و دومین شهر تاریخی جهان نیز به شمار می&zwnj;رود. استان یزد به دلیل ویژگی&zwnj;های تاریخی و طبیعی و همچنین معماری آن مورد توجه گردشگران داخلی و خارجی قرار گرفته است. از جمله جازبه&zwnj;های گردشگری این استان می&zwnj;توان موارد زیر را نام برد. آتشکده یزد معروف به آتش ورهرام با معماری هخامنشیان که آتش درون آن، به اهمیت آن افزوده است، آتشی که از 1520 سال پیش تاکنون روشن مانده است. آب انبار شش بادگیری، مجموعه امیر چقماق، باغ تاریخی دولت آباد که قدمت آن به زمان زندیه برمی&zwnj;گردد، مدرسه ضیائیه که به زندان اسکندر شهرت یافته است، خانه لاری&zwnj;ها با قدمت عصر قاجار، نارین قلعه که به نارنج قلعه نیز معروف است و از مهمترین آثار تاریخی پیش از اسلام در این استان بشمار می&zwnj;رود، چاپارخانه میبد یزد با قدمت عصر هخامنشیان، قدیمی&zwnj;ترین مسجد ایران یا شاید جهان به نام مسجد جامع فهرج که بنای آن تاکنون دست نخورده باقی مانده است، زیارتگاه زرتشتیان به نام زیارتگاه چک چک اردکان، دخمه زرتشتیان یزد، تنها بنای مدور ایران را می&zwnj;توان در کاروانسرای زین&zwnj;الدین مهریز مشاهده کرد، یخچال خشتی میبد با قدمت عصر قاجار، در شهرستان تفت می&zwnj;توان جاذبه&zwnj;های طبیعی ییلاقات میان کوه را مشاهده کرد، ییلاقات پشت کوه و پیش کوه، غار مقدس زرتشتیان به نام غار باستانی یزدان.\', \'یزد\', \'[\"یزد\",\"استان یزد\"]\', \'31.920190351171215\', \'54.36046389774026\', NULL, NULL);
        ');
